<style lang="stylus">
@import url('https://fonts.googleapis.com/css2?family=Jura:wght@300;500;700&display=swap');
@import url('assets/vars.css')
body
	font-family Jura, sans-serif
	display flex
	align-items center
	justify-content center
	height 100%
	width 100%
	min-height 100vh
	min-width 100vw
	margin 0
	padding 0
	background #999 url('assets/background.jpg') no-repeat center center fixed
	background-size 100% auto

#app
	-webkit-font-smoothing antialiased
	-moz-osx-font-smoothing grayscale
	color var(--color-light)
	width calc(1280px - 4rem)
	height calc(720px - 4rem)
	background #000
	display flex
	flex-direction column
	padding 2rem
	border-radius 1rem
	border 1px solid var(--color-light)
.page-view
	flex 1
	padding-bottom 1rem
	display flex
	align-items stretch
	justify-content stretch
	> *
		flex 1
nav
	background-color var(--color-box-bg)
	margin auto 0 0
	padding 0
	border-radius 1rem
	ul
		display flex
		justify-content center
		align-items center
		list-style-type none
		margin 0
		padding 0
		li
			display flex
			align-items center
			justify-content center
			flex 1
			a
